import React, { useState } from 'react';
import "./Home.css"
import { ReactComponent as Sun } from "../assests/Sun.svg";
import { ReactComponent as Moon } from "../assests/Moon.svg";

function Home() {

  const [theme, setTheme] = useState('light');
  const [inputValue, setInputValue] = useState('');
  const [listimg, setListImg] = useState([]);
  
  const setDarkMode = () => {
    document.querySelector('body').setAttribute('data-theme', 'dark');
    localStorage.setItem('selectedTheme', 'dark');
    setTheme('dark'); // Update state
  };

  const setLightMode = () => {
    document.querySelector('body').setAttribute('data-theme', 'light');
    localStorage.setItem('selectedTheme', 'light');
    setTheme('light'); // Update state
  };


  const toggleTheme = (e) => {
    if (e.target.checked) setDarkMode();
    else setLightMode();
  };

    const api= "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";
    
    const ain= document.querySelector('.ain');


    const getain = async () => {
      const inp = document.getElementById('inp');
      // إجراء الطلب
      const methods = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${api}`
        },
        body: JSON.stringify({
          prompt: inp.value, // تغيير "sea" إلى "prompt"
          n: 3,
          size: "256x256"
        })
      };
    
      try {
        const res = await fetch("https://api.openai.com/v1/images/generations", methods);
    
        if (!res.ok) {
          const error = await res.json();
          console.error('خطأ في API OpenAI:', error.error.message);
          return;
        }
    
        // تحليل الاستجابة كملف JSON
    

    // استخدام listimg في هذا النطاق
    const data = await res.json();
    const listimg = data.data;

    // حفظ الصور في الحالة
    setListImg(listimg);

  } catch (error) {
    console.error('خطأ:', error.message);
  }
};
    

  return (
    <section>
   <div className='tit'>
        <h1>AI TEST</h1>
        <div className='dark_modes'>
          <input
            className='dark_mode_inputs'
            type='checkbox'
            id='darkmode-toggle'
            onChange={toggleTheme}
            checked={theme === 'dark'} // Reflect theme in the checkbox
          />
          <label className='dark_mode_labels' htmlFor='darkmode-toggle'>
            <Sun />
            <Moon />
          </label>
        </div>
      </div>

      <div className='half'>
        <h2>AI Images Generator</h2>
      </div>

      <div className='ain'>
        {/* استخدام map لعرض الصور */}
        {listimg.map(photo => (
          <div key={photo.id}><img src={photo.url} alt={`AI Image ${photo.id}`} /></div>
        ))}
      </div>


      <div className='sea'>
  <input id='inp' value={inputValue} onChange={(e) => setInputValue(e.target.value)} />
  <button onClick={getain}>Generate</button>
</div>
    </section>
  )
}


export default Home;
